    static ArrayList<Content> getListData() {
        ArrayList<Content> list = new ArrayList<>();
        for (int position = 0; position < contentsName.length; position++) {
            Content content = new Content();
            content.setName(contentsName[position]);
            content.setType(contentType[position]);
            content.setComplexity(contentComplexity[position]);
            content.setDetail(contentDetails[position]);
            content.setPhoto(contentsImages[position]);
            list.add(content);
        }
        return list;
    }
